import React, { useState } from 'react';
import { Transition } from "react-transition-group";
import Header from './components/header';
import Contact from './components/contact';
import About from './components/about';
import Projects from './components/projects';
import './app.css'


const AComponent = ({ in: inProp, component , headerButton, HeaderButtonToggled,}) => (
    <Transition
        in={inProp}
        timeout={{
            appear: 100,
            enter: 300,
            exit: 300
        }}
        appear
        onExit={()=>HeaderButtonToggled(headerButton, false)}
    >
        {state => (
            <div style={{
                ...defaultStyle,
                ...transitionStyles[state]
            }}>
                {component}
            </div>
        )}
    </Transition>
);

const defaultStyle = {
    transition: `transform 200ms, opacity 200ms ease`,
    opacity: 1
};

const transitionStyles = {
    entering: { transform: 'scale(0.5)', opacity: 0 },
    entered: { transform: 'scale(1)', opacity: 1 },
};


const App = () => {

    const [WhichButtonToggled, SetButton] = useState([About, Projects, Contact])
    const [ButtonPressed, SetPressed] = useState(false)



    const HeaderButtonToggled = (button, clicked) => {
        SetPressed(clicked)
        SetButton(clicked)
    }

        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header

                    />
                </div>
                <div className="center-element">
                    <AComponent
                        in={ButtonPressed}
                        component={<About />}
                        HeaderButtonToggled={HeaderButtonToggled[0]}
                        AboutPressed={ButtonPressed}
                    />
                </div>
            </div>
        )
}

export default App
