import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { Transition } from "react-transition-group";


const AComponent = ({ in: inProp, component, headerToggle, headerButtonToggled}) => (
    <Transition
      in={inProp}
      timeout={{
        appear: 100,   
        enter: 300,
        exit: 300
      }}
      appear
      onExiting = {() =>headerButtonToggled(!headerToggle, component.name)}
    >
      {state => (
        <div style={{
            ...defaultStyle,
            ...transitionStyles[state]}}>
          {component}
        </div>
      )}
    </Transition>
  );


const defaultStyle = {
    transition: `transform 200ms, opacity 200ms ease`,
    opacity: 1
};

const transitionStyles = {
    entering: { transform: 'scale(0.5)', opacity: 0 },
    entered: { transform: 'scale(1)', opacity: 1 },
    exiting: { opacity: 0 },
    exited: { opacity: 0 }
};


const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "About") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<About/>}
                headerButtonToggled={headerButtonToggled}
                 />
                </div>
            </div>
        )
    }

    if (headerButton === "Contact") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<Contact/>} 
                headerButtonToggled={headerButtonToggled}
                />
                </div>
            </div>
        )
    }

    if (headerButton === "Projects") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<Projects/>} 
                headerButtonToggled={headerButtonToggled}
                />
                </div>
            </div>
        )
    }

    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
            <About/>
            </div>
        </div>
    )
}

export default Main
