import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { Transition, CSSTransition } from "react-transition-group";


const AComponent = ({ in: inProp }) => {
    return (
      <CSSTransition
        unmountOnExit
        in={inProp}
        timeout={{ appear: 0, enter: 0, exit: 300 }}
        classNames='roll'
        appear
      >
        <div>Gator</div>
      </CSSTransition>
    );
  };



const defaultStyle = {
    transition: `transform 200ms, opacity 200ms ease`,
    opacity: 1
};

const transitionStyles = {
    entering: { transform: 'scale(0.5)', opacity: 0 },
    entered: { transform: 'scale(2.0)', opacity: 1 },
    exiting: { opacity: 0 },
    exited: { opacity: 0 }
};


const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "Contact") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent in={headerToggle} />
                    <Contact />
                </div>
            </div>
        )

    }

    if (headerButton === "Projects") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent in={headerToggle} />
                    <Projects />
                </div>
            </div>
        )
    }

    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
                            <AComponent in={headerToggle} />
                <About />
            </div>
        </div>
    )
}

export default Main
