import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { CSSTransition } from "react-transition-group";
import {Alert} from 'react-bootstrap'


const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "About" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>

                <div className="center-element">
                    <CSSTransition
                        in={headerToggle}
                        timeout={300}
                        classNames="alert"
                        unmountOnExit
                    >
                            <Alert
          variant="primary"
          dismissible
          onClose={() => setShowMessage(false)}
        >
          <Alert.Heading>
            Animated alert message
          </Alert.Heading>
          <p>
            This alert message is being transitioned in and
            out of the DOM.
          </p>
          </Alert>
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Contact" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <CSSTransition
                        in={headerToggle}
                        timeout={300}
                        classNames="my-node"
                        unmountOnExit
                        onEnter={() => headerButtonToggled(false, "About")}
                        onExited={() => headerButtonToggled(true, "About")}
                    >
                        {<Contact />}
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Projects" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                </div>
            </div>
        )
    }
    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
                <About />
            </div>
        </div>
    )
}

export default Main
