import React, { useState } from 'react'
import axios from 'axios'
import './contact.css'

const Contact = () => {

    const [newName, setNewName] = useState('')
    const [newEmailAddress, setEmailAddress] = useState('')
    const [newMessage, setMessage] = useState('')

    const handleNameChange = (event) => {
        console.log(event.target.value)
        setNewName(event.target.value)
    }

    const handleEmailChange = (event) => {
        console.log(event.target.value)
        setEmailAddress(event.target.value)
    }

    const handleMessageChange = (event) => {
        console.log(event.target.value)
        setMessage(event.target.value)
    }

    const handleContact = (event, newName, newEmailAddress, newMessage) => {
        console.log("in handle contact")
        event.preventDefault();

        const contactData = {name: newName, email: newEmailAddress, message: newMessage}

        axios({
            method: "POST", 
            url:"http://localhost:3000", 
            data:  contactData
          }).then((response)=>{
            if (response.data.status === 'success'){
              alert("Message Sent."); 
              this.resetForm()
            }else if(response.data.status === 'fail'){
              alert("Message failed to send.")
            }
          })

          reset()
    }

    const reset = () =>{
        setNewName('')
        setEmailAddress('')
        setMessage('')
    }

    return (
        <div className="contact-container" method="POST">
            <form id="contact-form" onSubmit={handleContact}>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input type="text" className="form-control" value={newName} onChange={handleNameChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label>
                    <input type="email" className="form-control" aria-describedby="emailHelp" value={newEmailAddress} onChange={handleEmailChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="message">Message</label>
                    <textarea className="form-control" rows="5" value={newMessage} onChange={handleMessageChange} />
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary">Submit</button>
                </div>
            </form>
        </div>
    )
}

export default Contact
