import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { CSSTransition } from "react-transition-group";


const AComponent = ({ in: inProp, headerButtonToggled, component}) => (
    <CSSTransition 
        in={inProp} 
        timeout={200} 
        classNames="my-node"
        onEnter={() => headerButtonToggled(false)}
        onExited={() => headerButtonToggled(true)}
        >
    <div>
      {component}
    </div>
  </CSSTransition>
  );


const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "About") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent in={headerToggle} headerButtonToggled ={headerButtonToggled} component= {<About />} />
                </div>
            </div>
        )
    }

    if (headerButton === "Contact" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent in={headerToggle} headerButtonToggled ={headerButtonToggled} component= {<Contact />} />
                </div>
            </div>
        )
    }

    if (headerButton === "Projects") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent in={headerToggle} component= {<Projects />} />
                </div>
            </div>
        )
    }

    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
            <About/>
            </div>
        </div>
    )
}

export default Main
