import React, { useState } from 'react';
import Header from './components/header';
import Contact from './components/contact';
import About from './components/about';
import Projects from './components/projects';
import './app.css'
import { Transition } from "react-transition-group";


const AComponent = ({ in: inProp, component, headerButton, headerButtonToggled}) => (
    <Transition
      in={inProp}
      timeout={{
        appear: 100,   
        enter: 300,
        exit: 300
      }}
      appear
      onExiting = {headerButtonToggled(!headerButton)}
    >
      {state => (
        <div style={{
            ...defaultStyle,
            ...transitionStyles[state]}}>
          {component}
        </div>
      )}
    </Transition>
  );

const defaultStyle = {
    transition: `transform 200ms, opacity 200ms ease`,
    opacity: 1
};

const transitionStyles = {
    entering: { transform: 'scale(0.5)', opacity: 0 },
    entered: { transform: 'scale(1)', opacity: 1 },
};


const App = ({ headerToggle, headerButtonToggled, headerButton }) => {

    const [AboutPressed, SetAboutToggle] = useState(false)
    const [ProjectsPressed, SetProjectsToggle] = useState(false)
    const [ContactPressed, SetContactToggle] = useState(false)

    const AboutButtonToggled = (clicked) => {
      SetAboutToggle(clicked)
    }

    const ProjectsToggled = (clicked) => {
      SetProjectsToggle(clicked)
    }

    const ContactButtonToggled = (clicked) => {
      SetContactToggle(clicked)
    }


    if (headerButton === "About") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<About/>}
                SetAboutToggle={SetAboutToggle}
                AboutPressed={AboutPressed}
                 />
                </div>
            </div>
        )
    }

    if (headerButton === "Contact") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<Contact/>} 
                headerButtonToggled={headerButtonToggled}
                headerButton={headerButton}
                />
                </div>
            </div>
        )
    }

    if (headerButton === "Projects") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <AComponent 
                in={headerToggle} 
                component={<Projects/>} 
                headerButtonToggled={headerButtonToggled}
                headerButton={headerButton}
                />
                </div>
            </div>
        )
    }

    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
            <About/>
            </div>
        </div>
    )
}

export default App
