import React, { useState } from 'react';
import Header from './components/header';
import Contact from './components/contact';
import About from './components/about';
import Projects from './components/projects';
import './app.css'
import { Transition } from "react-transition-group";


const AComponent = ({ in: inProp, component , headerButtonToggled, headerButton}) => (
    <Transition
        in={inProp}
        timeout={{
            appear: 100,
            enter: 300,
            exit: 300
        }}
        appear
        onExiting={()=>headerButtonToggled(!headerButton)}
    >
        {state => (
            <div style={{
                ...defaultStyle,
                ...transitionStyles[state]
            }}>
                {component}
            </div>
        )}
    </Transition>
);

const defaultStyle = {
    transition: `transform 200ms, opacity 200ms ease`,
    opacity: 1
};

const transitionStyles = {
    entering: { transform: 'scale(0.5)', opacity: 0 },
    entered: { transform: 'scale(1)', opacity: 1 },
};


const App = () => {

    const [AboutPressed, SetAboutToggle] = useState(false)
    const [ProjectsPressed, SetProjectsToggle] = useState(false)
    const [ContactPressed, SetContactToggle] = useState(false)

    const AboutButtonToggled = (clicked) => {
        SetAboutToggle(clicked)
    }

    const ProjectsToggled = (clicked) => {
        SetProjectsToggle(clicked)
    }

    const ContactButtonToggled = (clicked) => {
        SetContactToggle(clicked)
    }


    if (AboutPressed) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        AboutPressed={AboutPressed}
                        AboutButtonToggled={AboutButtonToggled}
                        ProjectsPressed={ProjectsPressed}
                        ProjectsToggled={ProjectsToggled}
                        ContactPressed={ContactPressed}
                        ContactButtonToggled={ContactButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <AComponent
                        in={AboutPressed}
                        component={<About />}
                        AboutButtonToggled={AboutButtonToggled}
                        AboutPressed={AboutPressed}
                    />
                </div>
            </div>
        )
    }

    if (ProjectsPressed) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        AboutPressed={AboutPressed}
                        AboutButtonToggled={AboutButtonToggled}
                        ProjectsPressed={ProjectsPressed}
                        ProjectsToggled={ProjectsToggled}
                        ContactPressed={ContactPressed}
                        ContactButtonToggled={ContactButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <AComponent
                        in={ProjectsPressed}
                        component={<Projects />}
                        ProjectsToggled={ProjectsToggled}
                        ProjectsPressed={ProjectsPressed}
                    />
                </div>
            </div>
        )
    }

    if (ContactPressed) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        AboutPressed={AboutPressed}
                        AboutButtonToggled={AboutButtonToggled}
                        ProjectsPressed={ProjectsPressed}
                        ProjectsToggled={ProjectsToggled}
                        ContactPressed={ContactPressed}
                        ContactButtonToggled={ContactButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <AComponent
                        in={ContactPressed}
                        component={<Contact />}
                        ContactButtonToggled={ContactButtonToggled}
                        ContactPressed={ContactPressed}
                    />
                </div>
            </div>
        )
    }


    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    AboutPressed={AboutPressed}
                    AboutButtonToggled={AboutButtonToggled}
                    ProjectsPressed={ProjectsPressed}
                    ProjectsToggled={ProjectsToggled}
                    ContactPressed={ContactPressed}
                    ContactButtonToggled={ContactButtonToggled}
                />
            </div>
            <div className="center-element">
                <About />
            </div>
        </div>
    )
}

export default App
