import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { CSSTransition } from "react-transition-group";


const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "About" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>

                <div className="center-element">
                    <CSSTransition
                        in={headerToggle}
                        timeout={300}
                        classNames="my-node"
                        unmountOnExit
                    >
                        {<About />}
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Contact" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                <CSSTransition
                        in={headerToggle}
                        timeout={300}
                        classNames="my-node"
                        unmountOnExit
                        onEnter={() => headerButtonToggled(false, "About")}
                        onExited={() => headerButtonToggled(true, "About")}
                    >
                        {<Contact />}
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Projects" && headerToggle) {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                </div>
            </div>
        )
    }
    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
                <About />
            </div>
        </div>
    )
}

export default Main
