import React from 'react'
import Header from './header';
import Contact from './contact';
import About from './about';
import Projects from './projects';
import './main.css'
import { CSSTransition } from "react-transition-group";




const Main = ({ headerToggle, headerButtonToggled, headerButton }) => {

    if (headerButton === "About") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <CSSTransition
                        in={headerToggle}
                        timeout={200}
                        classNames="my-node"

                    >
                        <div>
                            <About />
                        </div>
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Contact") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <CSSTransition
                        in={headerToggle}
                        timeout={200}
                        classNames="my-node"
                        onExiting={() => headerButtonToggled(false)}

                    >
                        <div>
                            <Contact />
                        </div>
                    </CSSTransition>
                </div>
            </div>
        )
    }

    if (headerButton === "Projects") {
        return (
            <div className="main-wrapper">
                <div className="page-header">
                    <Header
                        headerToggle={headerToggle}
                        headerButtonToggled={headerButtonToggled}
                    />
                </div>
                <div className="center-element">
                    <CSSTransition
                        in={headerToggle}
                        timeout={200}
                        classNames="my-node"

                    >
                        <div>
                            <About />
                        </div>
                    </CSSTransition>
                </div>
            </div>
        )
    }

    return (
        <div className="main-wrapper">
            <div className="page-header">
                <Header
                    headerToggle={headerToggle}
                    headerButtonToggled={headerButtonToggled}
                />
            </div>
            <div className="center-element">
                <About />
            </div>
        </div>
    )
}

export default Main
