import './app.css'
import Header from './components/header';
import Contact from './components/contact';
import About from './components/about';
import Projects from './components/projects';

import React, { useState, useEffect } from 'react';
import { CSSTransition } from "react-transition-group";

const App = () => {

    const [buttonPressed, setButton] = useState("About")
    const [buttonToggled, setToggle] = useState(false)

    const headerButtonPressed = (button) => {
        setButton(button)
    }

    useEffect(() => {
        setToggle(true)
        console.log("Resource changed")
    },[buttonPressed])


    if (buttonPressed === "About") {

        return (

            <div className="main-wrapper">

                <div className="page-header">
                    <Header headerButtonPressed={headerButtonPressed}/>
                </div>

                <CSSTransition 
                in={buttonToggled} 
                timeout={300} 
                classNames="my-node" 
                >
                <div className="center-element">
                    <About />
                </div>
                </CSSTransition>
            </div>
        )
    }

    if (buttonPressed === "Projects") {

        return (

            <div className="main-wrapper">

                <div className="page-header">
                    <Header headerButtonPressed={headerButtonPressed}/>
                </div>

                <CSSTransition 
                in={buttonToggled} 
                timeout={300} 
                classNames="my-node" 
                >
                <div className="center-element">
                    <Projects />
                </div>
                </CSSTransition>
            </div>
        )
    }

    if (buttonPressed === "Contact") {

        return (

            <div className="main-wrapper">

                <div className="page-header">
                    <Header headerButtonPressed={headerButtonPressed}/>
                </div>

                <CSSTransition 
                in={buttonToggled} 
                timeout={300} 
                classNames="my-node" 
                >
                <div className="center-element">
                    <Contact />
                </div>
                </CSSTransition>
            </div>
        )
    }
}

export default App
